import React, { useEffect, useState } from 'react'
import axios from 'axios';

const ShippingTableData = () => {
  const [shippingData, setShippingData] = useState([]);

  useEffect(() => {
    // Fetch data from the backend
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:3000/shippingdata");
        // Destructure the response data
        const { data } = response;
        // Update the state with the fetched data
        setShippingData(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (productid,customerid) => {
      try {
          // Send a delete request to the backend to delete the customer with the given ID
          await axios.delete(`http://127.0.0.1:3000/home/cancelshipping/${productid}?customerid=${customerid}`);

          // Remove the deleted shipping data from the state
          setShippingData(shippingData.filter(shippingData => shippingData.customerId !== customerid && sh.productId !== productid));
      } catch (error) {
          console.error('Error deleting customer:', error);
      }
  };

  return (
    <>
      {shippingData.map((data) => (
        <tr>
          <td>{data.address}</td>
          <td>{data.city}</td>
          <td>{data.pincode}</td>
          <td>{data.customerId}</td>
          <td>{data.productId}</td>
          <input type="button" value="X" className="cnclBtn" onClick={()=>{handleDelete(data.productId,data.customerId)}} />
        </tr>
      ))}
    </>
  );
}

export default ShippingTableData;
