import React, { useEffect, useState } from "react";
import axios from "axios";
import "@fortawesome/fontawesome-free/css/all.min.css";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const CustomerTableData = () => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    // Fetch data from the backend
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://127.0.0.1:3000/home/customers"
        );
        // Destructure the response data
        const { data } = response;
        // Update the state with the fetched data
        setCustomers(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (customerid) => {
    try {
      // Send a delete request to the backend to delete the customer with the given ID
      await axios.delete(
        `http://127.0.0.1:3000/home/customersdata/${customerid}`
      );

      // Remove the deleted customer from the state
      setCustomers(
        customers.filter((customer) => customer._id !== String(customerid))
      );
    } catch (error) {
      console.error("Error deleting customer:", error);
    }
  };

  return (
    <>
      {customers.map((customer) => (
        <tr key={customer._id}>
          <td>{customer.name}</td>
          <td>{customer.city}</td>
          <td>{customer.phone}</td>
          <td>{customer.email}</td>
          <td>{customer._id}</td>
          <td>
            <button onClick={() => handleDelete(customer._id)}>Delete</button>
            <EditButton customer={customer} />
          </td>
        </tr>
      ))}
    </>
  );
};

const EditButton = ({ customer }) => {
  const [showForm, setShowForm] = useState(false);

  const handleEditClick = () => {
    setShowForm(!showForm);
  };

  return (
    <>
      <button onClick={handleEditClick}>
        <i className="fas fa-edit"></i> Edit
      </button>

      {showForm && (
        <div className="overlay">
          <Box
            className="centered-form"
            component="form"
            sx={{
              "& .MuiTextField-root": { m: 1, width: "25ch" },
            }}
            noValidate
            autoComplete="off"
          >
            <div>
              <TextField
                id="outlined-name"
                label="Name"
                defaultValue={customer.name}
              />
              <TextField
                id="outlined-city"
                label="City"
                defaultValue={customer.city}
              />
            </div>
            <div>
              <TextField
                id="outlined-phone"
                label="Phone"
                defaultValue={customer.phone}
              />
              <TextField
                id="outlined-email"
                label="Email"
                defaultValue={customer.email}
              />
            </div>
            <div>
              <TextField
                id="outlined-id"
                label="ID"
                defaultValue={customer._id}
                variant="standard"
                disabled
              />
            </div>
          </Box>
        </div>
      )}
    </>
  );
};

export default CustomerTableData;
