import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "@fortawesome/fontawesome-free/css/all.min.css";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const CustomerTableData = () => {
    const [customers, setCustomers] = useState([]);
    const [showForm, setShowForm] = useState();


    useEffect(() => {
        // Fetch data from the backend
        const fetchData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:3000/home/customers');
                // Destructure the response data
                const { data } = response;
                // Update the state with the fetched data
                setCustomers(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);



    const EditButton = () => {
       setShowForm(false);

        <button onClick={setShowForm(true)}>
          <i className="fas fa-edit"></i> Edit
        </button>;

        return (
          <>
                {showForm && (
                    <Box
                        component="form"
                        sx={{
                            "& .MuiTextField-root": { m: 1, width: "25ch" },
                        }}
                        noValidate
                        autoComplete="off"
                    >
                        <div>
                            <TextField
                                error
                                id="outlined-error"
                                label="Error"
                                defaultValue='name'
                            />
                            <TextField
                                error
                                id="outlined-error-helper-text"
                                label="Error"
                                defaultValue='city'
                                helperText="Incorrect entry."
                            />
                        </div>
                        <div>
                            <TextField
                                error
                                id="filled-error"
                                label="Error"
                                defaultValue='phone'
                                variant="filled"
                            />
                            <TextField
                                error
                                id="filled-error-helper-text"
                                label="Error"
                                defaultValue='email'
                                helperText="Incorrect entry."
                                variant="filled"
                            />
                        </div>
                        <div>
                            <TextField
                                error
                                id="standard-error"
                                label="Error"
                                defaultValue='id'
                                variant="standard"
                            />
                        </div>
                    </Box>
                )} 
            </>
      );


    };


    const handleDelete = async (customerid) => {

        try {
            // Send a delete request to the backend to delete the customer with the given ID
            await axios.delete(`http://127.0.0.1:3000/home/customersdata/${customerid}`);

            // Remove the deleted customer from the state
            setCustomers(customers.filter(customer => customer._id !== String(customerid)));
        } catch (error) {
            console.error('Error deleting customer:', error);
        }
    };

    return (
      <>
        {customers.map((customer) => (
          <tr>
            <td>{customer.name}</td>
            <td>{customer.city}</td>
            <td>{customer.phone}</td>
            <td>{customer.email}</td>
            <td>{customer._id}</td>
            <input
              type="button"
              value="Delete"
              className="cnclBtn"
              onClick={() => handleDelete(customer._id)}
            />

          </tr>
        ))}
      </>
    );
}

export default CustomerTableData;
