import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "@fortawesome/fontawesome-free/css/all.min.css";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const ProductTableData = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Fetch data from the backend
        const fetchData = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:3000/newproducts');
                // Destructure the response data
                const { data } = response;
                // Update the state with the fetched data
                setProducts(data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);


    const handleDelete = async (productid) => {
        try {
            // Send a delete request to the backend to delete the customer with the given ID
            await axios.delete(`http://127.0.0.1:3000/home/products/${productid}`);

            // Remove the deleted customer from the state
            setProducts(products.filter(product => product._id !== String(productid)));
        } catch (error) {
            console.error('Error deleting customer:', error);
        }
    };

    return (
      <>
        {products.map((product) => (
          <tr>
            <td>{product.productName}</td>
            <td>{product.quantity}</td>
            <td>{product.price}</td>
            <td>{product.MRP}</td>
            <td>{product._id}</td>
            <td>
              <input
                type="button"
                value="Delete"
                className="cnclBtn"
                onClick={() => handleDelete(product._id)}
              />
              <EditButton product={product} />
            </td>
          </tr>
        ))}
      </>
    );
}


const EditButton = ({ product }) => {
  const [showForm, setShowForm] = useState(false);

  const handleEditClick = () => {
    setShowForm(true);
  };

  return (
    <>
      <button onClick={handleEditClick}>
        <i className="fas fa-edit"></i> Edit
      </button>

      {showForm && (
        <div className="overlay">
          <Box
            className="centered-form"
            component="form"
            sx={{
              "& .MuiTextField-root": { m: 1, width: "25ch" },
            }}
            noValidate
            autoComplete="off"
          >
            <div>
              <TextField
                id="outlined-name"
                              label="Product name"
                              name='productName'
                defaultValue={product.productName}
              />
              <TextField
                id="outlined-city"
                              label="Quantity"
                              name='quantity'
                defaultValue={product.quantity}
              />
            </div>
            <div>
              <TextField
                id="outlined-phone"
                              label="Price"
                              name='price'
                defaultValue={product.price}
              />
              <TextField
                id="outlined-email"
                label="MRP"
                defaultValue={product.MRP}
              />
            </div>
            <div>
              <TextField
                id="outlined-id"
                label="ID"
                defaultValue={product._id}
                variant="standard"
                disabled
              />
            </div>
            <input
              type="button"
              value="Cancel"
              onClick={() => setShowForm(false)}
            />
            <input type="button" value="Save" />
          </Box>
        </div>
      )}
    </>
  );
};



export default ProductTableData;
