import React, { useEffect, useState } from "react";
import axios from "axios";
import "@fortawesome/fontawesome-free/css/all.min.css";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";

const CustomerTableData = () => {
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    // Fetch data from the backend
    const fetchData = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:3000/home/customers");
        // Destructure the response data
        const { data } = response;
        // Update the state with the fetched data
        setCustomers(data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleDelete = async (customerid) => {
    try {
      // Send a delete request to the backend to delete the customer with the given ID
      await axios.delete(`http://127.0.0.1:3000/home/customersdata/${customerid}`);

      // Remove the deleted customer from the state
      setCustomers(customers.filter((customer) => customer._id !== String(customerid)));
    } catch (error) {
      console.error("Error deleting customer:", error);
    }
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>City</th>
          <th>Phone</th>
          <th>Email</th>
          <th>ID</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {customers.map((customer) => (
          <tr key={customer._id}>
            <td>{customer.name}</td>
            <td>{customer.city}</td>
            <td>{customer.phone}</td>
            <td>{customer.email}</td>
            <td>{customer._id}</td>
            <td>
              <button onClick={() => handleDelete(customer._id)}>Delete</button>
              <EditButton customer={customer} onCustomerUpdate={(updatedCustomer) => {
                setCustomers(customers.map(c => c._id === updatedCustomer._id ? updatedCustomer : c));
              }} />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

const EditButton = ({ customer, onCustomerUpdate }) => {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: customer.name,
    city: customer.city,
    phone: customer.phone,
    email: customer.email,
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSave = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch(`http://localhost:3000/updateCustomer/${customer._id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();
      alert(data.message); // Display success message

      onCustomerUpdate({
        ...customer,
        ...formData,
      });

      setShowForm(false);
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred while updating the customer.");
    }
  };

  const handleEditClick = () => {
    setShowForm(true);
  };

  return (
    <>
      <button onClick={handleEditClick}>
        <i className="fas fa-edit"></i> Edit
      </button>

      {showForm && (
        <div className="overlay">
          <Box
            className="centered-form"
            component="form"
            sx={{
              "& .MuiTextField-root": { m: 1, width: "25ch" },
            }}
            noValidate
            autoComplete="off"
          >
            <div>
              <TextField
                id="outlined-name"
                label="Name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
              />
              <TextField
                id="outlined-city"
                label="City"
                name="city"
                value={formData.city}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <TextField
                id="outlined-phone"
                label="Phone"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
              />
              <TextField
                id="outlined-email"
                label="Email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
              />
            </div>
            <div>
              <TextField
                id="outlined-id"
                label="ID"
                value={customer._id}
                variant="standard"
                disabled
              />
            </div>
            <button type="button" onClick={() => setShowForm(false)}>
              Cancel
            </button>
            <button type="button" onClick={handleSave}>
              Save
            </button>
          </Box>
        </div>
      )}
    </>
  );
};

export default CustomerTableData;
